import React, { useState, useEffect } from 'react';
import useSessionStorage from '../../utils/useSessionStorage'
import { BOT_STEPS, NEXT_STEP }  from '../../consts/chatConsts'
import { getStepDetails } from '../../utils/chatUtils';
import ChatHeader from '../ChatHeader/ChatHeader'
import ChatFooter from '../ChatFooter/ChatFooter';
import ChatContent from '../ChatContent/ChatContent';

import './Chat.css';


const Chat = () => {
    const [name, setName] = useSessionStorage('name', '')
    const [expression, setExpression] = useState('')
    // const [step, setStep] = useState(name ? BOT_STEPS.NICE_TO_SEE : BOT_STEPS.INTRO)
    // const [messages, setMessages] = useState([])

    const [data, setData] = useState({step: name ? BOT_STEPS.NICE_TO_SEE : BOT_STEPS.INTRO, messages: []})
console.log(data.messages)
    // useEffect(() => {
    //   const stepDetails = getStepDetails(data.step, name, expression)
    //  //   debugger
    //   if (!stepDetails?.isUserInput) { 
    //       setTimeout(() => {
    //         const nextStep = NEXT_STEP[data.step]
    //     //    debugger
    //     setData({step: nextStep, messages: [...data.messages, stepDetails]})
    //         //   setStep(nextStep)
    //         //   setMessages([...data.messages, stepDetails])
    //         }, 500)
    //     }

    //   },[data,name,expression])

    const sendMessageHandler = (newMessage) => {
        const nextStep =  NEXT_STEP[data.step]
        if (data.step === BOT_STEPS.GET_NAME) {
            setName(newMessage)
           // setStep(nextStep)
           setData({step: nextStep, messages: [...data.messages, {message: newMessage, isUserInput: true}]})
        } else if (data.step === BOT_STEPS.GET_MATH_EXPRESSION) {
            setExpression(newMessage)
           // setStep(nextStep)
            setData({step: nextStep, messages: [...data.messages, {message: newMessage, isUserInput: true}]})
        } else {
            setData({step: data.step, messages: [...data.messages, {message: newMessage, isUserInput: true}]})
            // setStep(data.step)
            // setMessages([...data.messages, {message: newMessage, isUserInput: true}])   
        }     
    }

  return (
    <div className="chat-container">
        <ChatHeader/>
        <ChatContent messages={data.messages}/>
        <ChatFooter onSend={sendMessageHandler}/>
    </div>
  );
}

export default Chat;
