import { BOT_STEPS } from "../consts/chatConsts";

export const getStepDetails = (step, name='', expression='') => {
    switch (step) {
        case BOT_STEPS.INTRO:
            return { message: `Hi, I’m Maya! Today you’re going to help me to ace my game.`, isUserInput: false }
        case BOT_STEPS.ASK_NAME:
            return { message: `Let’s start by telling me your name`, isUserInput: false }
        case BOT_STEPS.GET_NAME: 
            return {  isUserInput: true }
        case BOT_STEPS.NICE_TO_MEET:
            return { message: `Nice to meet you ${name}!`, isUserInput: false }
        case BOT_STEPS.NICE_TO_SEE:
            return { message: `Nice to see you again ${name}. Let’s pick this up from where we left off`, isUserInput: false }
        case BOT_STEPS.DESCRIBE_PROCESS:
            return { message: `Alright, this is how it’s going to work`, isUserInput: false }
        case BOT_STEPS.ASK_MATH_EXPRESSION:
            return { message: `List any mathematical expression you can think of - I’ll crunch it in no time`, isUserInput: false }
        case BOT_STEPS.GET_MATH_EXPRESSION: 
            return { isUserInput: true }
        case BOT_STEPS. MATH_EXPRESSION_RESULT:
            return  { message: calculateMathExpression(), isUserInput: false }
        case BOT_STEPS.BOT_FEEDBACK:
            return { message: `This was easy, give me something harder ;)`, isUserInput: false }
        default:
            return {}
    }
}

export const calculateMathExpression = (mathExpression) => {
    return "true"
}
