import React, { useState, useEffect } from 'react';
import useSessionStorage from '../../utils/useSessionStorage'
import { BOT_STEPS, NEXT_STEP }  from '../../consts/chatConsts'
import { getStepDetails } from '../../utils/chatUtils';
import ChatHeader from '../ChatHeader/ChatHeader'
import ChatFooter from '../ChatFooter/ChatFooter';
import ChatContent from '../ChatContent/ChatContent';

import './Chat.css';


const Chat = () => {
    const [name, setName] = useSessionStorage('name', '')
    const [expression, setExpression] = useState('')
    const [isPending, setIsPending] = useState(false)
    const [step, setStep] = useState(name ? BOT_STEPS.NICE_TO_SEE : BOT_STEPS.INTRO)
    const [messages, setMessages] = useState([])

    useEffect(() => {
      const stepDetails = getStepDetails(step, name, expression)
     //   debugger
      if (!stepDetails?.isUserInput) { 
          setTimeout(() => {
            const nextStep = NEXT_STEP[step]
        //    debugger
              setStep(nextStep)
              setIsPending(false)
              setMessages([...messages, stepDetails])
            }, 1000)
        }

      }, [])

    const sendMessageHandler = (newMessage) => {

        if(isPending){
            return
        }

        const nextStep =  NEXT_STEP[step]
        if (step === BOT_STEPS.GET_NAME) {
            setName(newMessage)
            setStep(nextStep)
            setIsPending(ture)
        } else if (step === BOT_STEPS.GET_MATH_EXPRESSION) {
            setExpression(newMessage)
            setStep(nextStep)
            setIsPending(ture)
        }

        setMessages([...messages, {message: newMessage, isUserInput: true}])     
    }

  return (
    <div className="chat-container">
        <ChatHeader/>
        <ChatContent messages={messages}/>
        <ChatFooter onSend={sendMessageHandler}/>
    </div>
  );
}

export default Chat;
