import React, { useState } from 'react';
import ChatHeader from '../ChatHeader/ChatHeader'
import useSessionStorage from '../../utils/useSessionStorage'
import './Chat.css';
import ChatFooter from '../ChatFooter/ChatFooter';

const Chat = props => {
    const [messages, setMessages] = useState([])
    const [name, setName] = useSessionStorage('name', '');

    const sendMessageHandler = (newMessage) => {
        setName(newMessage)
        setMessages([...messages, newMessage])
    }

  return (
    <div className="chat-container">
        <ChatHeader/>
        <div className='chat-content'>
         <div className='message-content'>referfer referfer referfer referfer referfer referfer referfer referfer referfer </div> 
         <div className='message-content'>referfer</div> 
         <div className='message-content'>referfer</div> 
         <div className='message-content'>referfer</div> 
         <div className='message-content'>referfer</div> 
         <div className='message-content'>referfer</div> 
         <div className='message-content'>referfer</div> 
         <div className='message-content'>referfer</div> 
         <div className='message-content'>referfer</div> 
         <div className='message-content'>111111111</div> 
        </div>
        <ChatFooter onSend={sendMessageHandler}/>
    </div>
  );
}

export default Chat;
