import React, { useState } from 'react';
import useSessionStorage from '../../utils/useSessionStorage'
import { BOT_STEPS, NEXT_STEP }  from '../../consts/chatConsts'
import { getBotMessageByStep } from '../../utils/chatUtils';
import ChatHeader from '../ChatHeader/ChatHeader'
import ChatFooter from '../ChatFooter/ChatFooter';
import ChatContent from '../ChatContent/ChatContent';

import './Chat.css';


const Chat = () => {
    const [name, setName] = useSessionStorage('name', '');
    const [step, setStep] = useState(name ? BOT_STEPS.NICE_TO_SEE : BOT_STEPS.INTRO)
    const [messages, setMessages] = useState([getBotMessageByStep(step, name)])

    useEffect(() => {
        const nextStep = NEXT_STEP[step]
        const { isUserInput } = BOT_STEPS[step]

      if (!isUserInput) { 
          setTimeout(() => setStep(nextStep), 600)
        }

      }, [messages])

    const sendMessageHandler = (newMessage) => {
        setName(newMessage)
        setMessages([...messages, newMessage])
    }

  return (
    <div className="chat-container">
        <ChatHeader/>
        <ChatContent messages={messages}/>
        <ChatFooter onSend={sendMessageHandler}/>
    </div>
  );
}

export default Chat;
