import React, { useState, useEffect } from 'react';
import useSessionStorage from '../../utils/useSessionStorage'
import { calculateMathExpression, BOT_STEPS } from '../../utils/chatUtils';
import ChatHeader from '../ChatHeader/ChatHeader'
import ChatFooter from '../ChatFooter/ChatFooter';
import ChatContent from '../ChatContent/ChatContent';

import './Chat.css';

const useTimeoutMessages = (timeout=1000, initialValue = [])  =>{

    const [innerMessages, setInnerMessages] = useState(initialValue)

    const setMessages = messages => {
        messages.map(message => 
        setTimeout(() => {
           setInnerMessages([...innerMessages, message])
          }, timeout)
        )
    }

    return [innerMessages, setMessages];
  }


const Chat = () => {
    const [name, setName] = useSessionStorage('name', '')
    const [isBot, setIsBot] = useState(true) 
    const [messages, setMessages] = useTimeoutMessages(1000,[])

    // useEffect(() => {
    //   /*const stepDetails = getStepDetails(step, name, expression)
    //  //   debugger
    //   if (!stepDetails?.isUserInput) { 
    //       setTimeout(() => {
    //         const nextStep = NEXT_STEP[step]
    //     //    debugger
    //           setStep(() => nextStep)
    //           setIsPending(() => false)
    //           setMessages([...messages, stepDetails])
    //         }, 1000)
    //     }*/



    //   }, [messages])

    useEffect(()=>{
        if (name) {
            setMessages([...BOT_STEPS.NICE_TO_SEE(name), ...BOT_STEPS.DESCRIBE_PROCESS()]) 
        }
        else {
            setMessages(BOT_STEPS.INTRO())
        }

        setIsBot(false)
    }, [])


    const sendMessageHandler = (newMessage) => {

        // We check if this is the user turn to give an input.
        // If we already have a name, we expect math expression. Otherwise, we expect a name and add the correct messages.
        if(!isBot){   
            setIsBot(false)

            if(!name){
                setName(newMessage)
                setMessages([
                            { message: newMessage, isUserInput: true },
                            ...BOT_STEPS.NICE_TO_MEET(newMessage), 
                            ...BOT_STEPS.DESCRIBE_PROCESS()])
            }
            else {
                setMessages([
                            { message: newMessage, isUserInput: true },
                            { message: calculateMathExpression(newMessage) }, 
                            ...BOT_STEPS.BOT_FEEDBACK()])
            }

           return
        }
        // Add user messages to the list even if it is not the user turn
        setMessages([ { message: newMessage, isUserInput: true }])

    }

  return (
    <div className="chat-container">
        <ChatHeader/>
        <ChatContent messages={messages}/>
        <ChatFooter onSend={sendMessageHandler}/>
    </div>
  );
}

export default Chat;
