export const BOT_STEPS = Object.freeze({
    INTRO: () => [{message: `Hi, I’m Maya! Today you’re going to help me to ace my game.`}, {message: `Let’s start by telling me your name`}], // V
    // ASK_NAME: null,
    // GET_NAME: null,
    NICE_TO_MEET: name => [{message: `Nice to meet you ${name}!`}],
    NICE_TO_SEE: name => [ {message: `Nice to see you again ${name}. Let’s pick this up from where we left off`}],  //V
    DESCRIBE_PROCESS: () => [{message: `Alright, this is how it’s going to work`}, {message: `List any mathematical expression you can think of - I’ll crunch it in no time`}], // V
    // ASK_MATH_EXPRESSION: null,
    // GET_MATH_EXPRESSION: null,
    // MATH_EXPRESSION_RESULT: null,
    BOT_FEEDBACK: () => [{message: `This was easy, give me something harder ;)`}]
})
