import React,  { useEffect, useState }from 'react'
import cx from 'classnames'
import userPic from '../../assets/user_avatar.png'
import MayasPic from '../../assets/mayas_avatar.png'

import './Message.css'

const Message = ({ currMessage, i, nextMessage }) => {
    const [messageToRender, setMessage] = useState(null)
    const {message, isUserInput} = messageToRender

    useEffect(() => {
        setTimeout(() => {
            //debugger
            setMessage(currMessage)
           }, 1000)
    })

    return (
        <div className={cx('message-container', {'user-side': isUserInput})} key={i}>
            <img className={cx('message-img', {'hidden' :nextMessage?.isUserInput === isUserInput && !!nextMessage})}  src={isUserInput ? userPic :MayasPic} alt=''/>
            <div className='message-content'>
                {message}
            </div> 
        </div>
    )
}

export default Message
